.information {
    display: flex;
    flex-direction: row;

    @media only screen and (max-width: $bp-mobile) {
        flex-direction: column;
        height: 90rem;
    }

    @media only screen and (max-width: $bp-minimum) {
        height: 80rem;
    }

    &_details {
        display: flex;
        flex-direction: column;
        margin-right: 8.3rem;

        @media only screen and (max-width: $bp-mobile) {
            width: 33rem;
            margin-left: 2rem;
            margin-top: 16rem;
            font-size: 2rem;
        }

        @media only screen and (max-width: $bp-minimum) {
            width: 28rem;
            margin-top: 12rem;
        }


        &_top {
            width: 43.1rem;
            height: 20.1rem;
            line-height: 6.4rem;
            margin-bottom: 3.9rem;

            @media only screen and (max-width: $bp-tablet) {
                width: 30rem;
                font-size: 2rem;
                margin-left: 3rem;
                margin-top: 3rem;
                line-height: 5rem;
                margin-bottom: 2rem;
            }

            @media only screen and (max-width: $bp-mobile) {
                width: 33rem;
                height: 10.4rem;
                margin: 0 auto;
                line-height: 4.4rem;
            }

            @media only screen and (max-width: $bp-minimum) {
                width: 28rem;
                height: 6.4rem;
                line-height: 3.4rem;
                margin-bottom: 2rem;
            }

        }

        &_bottom {
            width: 43.9rem;
            height: 18.7rem;
            font-size: 2.4rem;
            line-height: 4rem;
            margin-bottom: 8.1rem;

            @media only screen and (max-width: $bp-tablet) {
                width: 30rem;
                font-size: 2rem;
                margin-left: 3rem;
            }

            @media only screen and (max-width: $bp-mobile) {
                width: 32rem;
                height: 20.3rem;
                margin: 0 auto;
                margin-bottom: 3.1rem;
