    &_features {

        @media only screen and (max-width: $bp-tablet) {
            width: 30rem;
            font-size: 2rem;
        }

        @media only screen and (max-width: $bp-mobile) {
            margin: 0 auto;
            position: absolute;
            top: 45rem;
            left: 2.9rem;
