        @media only screen and (max-width: $bp-minimum) {
            width: 24rem;
            top: 40rem;
            left: 2rem;
        }

        &_logos {
            display: flex;
            flex-direction: row;
            gap: 4rem;
            margin-bottom: 6.5rem;

            @media only screen and (max-width: $bp-tablet) {
                margin-bottom: 3rem;
                margin-top: 3rem;

                display: flex;
                flex-direction: column;

            }

            @media only screen and (max-width: $bp-mobile) {
                width: 33rem;
                flex-direction: row;
            }

            @media only screen and (max-width: $bp-minimum) {
                width: 28rem;
            }

            &_img {
                @media only screen and (max-width: $bp-mobile) {
                    width: 13rem;
                    flex-direction: row;
                }

                @media only screen and (max-width: $bp-minimum) {
                    width: 12rem;
                    flex-direction: row;
                }
            }
        }

        &_container {
            @media only screen and (max-width: $bp-mobile) {
                position: relative;
            }

            &_list {
                list-style: none;
                font-size: 1.8rem;
                line-height: 2.7rem;
                width: 44rem;
                height: 25rem;

                @media only screen and (max-width: $bp-tablet) {
                    width: 30rem;

                }

                @media only screen and (max-width: $bp-mobile) {
                    height: 37.8rem;
                    position: absolute;
                    top: 30rem;
                }

                @media only screen and (max-width: $bp-minimum) {
                    width: 28rem;
                    top: 26rem;
                }

                display: flex;
                flex-direction: column;
                justify-content: space-between;
                gap: 3rem;
            }
        }
