sources.sass\\_form.scss:
.form {
    width: 54.7rem;
    height: 105rem;

    @media only screen and (max-width: $bp-mobile) {
        width: 37.5rem;
        height: 124rem;
    }

    @media only screen and (max-width: $bp-minimum) {
        width: 32rem;
        height: 124rem;
    }

    display: none;
    flex-direction: row;
    margin: 0 auto;

    position: absolute;
    top: 5rem;
    left: 42.9rem;

    @media only screen and (max-width: $bp-tablet) {
        left: 10rem;
    }

    @media only screen and (max-width: $bp-mobile) {
        left: 0;
        top: 0;
    }

    &_inputs {
        padding: 0.9rem 3.7rem 5rem 4.6rem;
        background: var(--color-white);
        margin: 0 auto;
        -webkit-box-shadow: 1px 5px 11px 4px rgba(0, 0, 0, 0.29);
        box-shadow: 1px 5px 11px 4px rgba(0, 0, 0, 0.29);

        &_headline {
            width: 38rem;
            height: 10.2rem;
            margin: 4rem auto 2rem auto;
            text-align: center;

            @media only screen and (max-width: $bp-mobile) {
                width: 30rem;
                margin: 0;
                margin-top: 7rem;
            }

            @media only screen and (max-width: $bp-minimum) {
                width: 24rem;
                margin: 0;
                margin-top: 7rem;
            }

        }

        &_name {
            width: 46.8rem;
            height: 5.5rem;
            margin-bottom: 5.3rem;

            @media only screen and (max-width: $bp-mobile) {
                width: 20rem;
            }
        }

        &_email_number {
            display: flex;
            flex-direction: row;
            gap: 2.4rem;

            @media only screen and (max-width: $bp-mobile) {
                flex-direction: column;
                width: 20rem;
            }
        }

        &_website {
            @media only screen and (max-width: $bp-mobile) {
                width: 20rem;
            }
        }
    }

    &_wrapper {

        &_captcha {
            display: flex;
            height: 6.5rem;
            margin: 3rem 0 2rem;
            align-items: center;
            justify-content: space-between;


            @media only screen and (max-width: $bp-mobile) {
                width: 28rem;
            }

            @media only screen and (max-width: $bp-minimum) {
                width: 22rem;
            }


            &_img {
                height: 100%;
                width: 34.5rem;
                user-select: none;
                color: var(--color-black);
                border-radius: 0.5rem;
                position: relative;
                background-image: url(../images/captcha-bg.png);
                font-size: 3rem;
                text-align: center;
                padding-top: 1.2rem;
                font-family: 'Noto Serif', serif;

                @media only screen and (max-width: $bp-mobile) {
                    width: 20rem;
                }

                img {
                    width: 100%;
                    height: 100%;
                    object-fit: cover;
                    border-radius: 0.5rem;
                    opacity: 0.95;
                }

                span {
                    position: absolute;
                    left: 50%;
                    top: 50%;
                    width: 100%;
                    color: var(--color-white);
                    font-size: 3.5rem;
                    text-align: center;
                    letter-spacing: 1rem;
                    transform: translate(-50%, -50%);
                    text-shadow: 0 0 0.2rem #b1b1b1;
                }
            }

            &_reloadbutton {
                width: 7.5rem;
                height: 100%;
                font-size: 2.5rem;

                @media only screen and (max-width: $bp-mobile) {
                    width: 6rem;
                }

                i {
                    transition: transform 0.3s ease;
                }

                i:hover {
                    transform: rotate(15deg);
                }
            }

            button {
                outline: none;
                border: none;
                color: var(--color-white);
                cursor: pointer;
                background: var(--color-red);
                border-radius: 0.5rem;
                transition: all 0.3s ease;
            }

            button:hover {
                background: var(--color-red-hover);
            }
        }

        &_input {
            width: 100%;
            height: 100%;
            outline: none;
            font-size: 2rem;
            border-radius: 0.4rem;
            display: flex;
            justify-content: space-between;
            margin-bottom: 2rem;

            position: relative;

            input {
                height: 6rem;
                width: 46.8rem;
                position: relative;
                border-radius: 0.4rem;
                border: 0.1rem solid var(--color-grey);
                cursor: pointer;

                @media only screen and (max-width: $bp-mobile) {
                    width: 28rem;
                }

                @media only screen and (max-width: $bp-minimum) {
                    width: 22rem;
                }
            }

            input:is(:focus, :valid) {
                padding-left: 1rem;
            }

            input:placeholder {
                color: var(--color-input-grey);
            }

            input:valid+.checkbutton {
                opacity: 1;
                pointer-events: auto;
            }

            .checkbutton {
                position: absolute;
                right: 0.7rem;
                top: 3rem;
                font-size: 1.7rem;
                height: 4.5rem;
                padding: 0 2rem;
                opacity: 0;
                pointer-events: none;
                transform: translateY(-50%);
                background-color: var(--color-red);
                border-radius: 0.4rem;
                border: none;
                color: var(--color-white);
                cursor: pointer;

                @media only screen and (max-width: $bp-mobile) {
                    right: 2.6rem;
                }
            }

            .checkbutton:hover {
                background-color: var(--color-red-hover);
            }
        }

        &_status {
            width: 46.8rem;
            display: none;
            font-size: 1.8rem;
            padding: 2rem;
            text-align: center;

            @media only screen and (max-width: $bp-tablet) {
                width: 46.8rem;
            }

            @media only screen and (max-width: $bp-mobile) {
                width: 28rem;
            }

            @media only screen and (max-width: $bp-minimum) {
                width: 22rem;
            }
        }
    }


}

